mapper.select:
select

from t_instock_log
where 1 = 1


		and companyId = #{instocklog.companyId,jdbcType=CHAR}


		and instockNum =
		#{instocklog.instockNum,jdbcType=VARCHAR}


		and instockBatchNum =
		#{instocklog.instockBatchNum,jdbcType=VARCHAR}


		and goodsId = #{instocklog.goodsId,jdbcType=CHAR}

SELECT t1.goodsId as id, t3.productName as text FROM t_instock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId where 1=1
and t1.companyId = #{companyid,jdbcType=CHAR}
and t1.instockNum like CONCAT('${yearmonth}','%' )
UNION SELECT t1.goodsId as id, t3.productName as text FROM t_outstock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId where 1=1
and t1.companyId = #{companyid,jdbcType=CHAR}
and t1.outstockNum like CONCAT('${yearmonth}','%' )

SELECT SUBSTRING(t1.instockNum FROM 1 FOR 6) as id, SUBSTRING(t1.instockNum FROM 1 FOR 6) as text FROM t_instock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId where 1=1
and t1.companyId = #{companyid,jdbcType=CHAR}
UNION SELECT SUBSTRING(t1.outstockNum FROM 1 FOR 6) as id, SUBSTRING(t1.outstockNum FROM 1 FOR 6) as text FROM t_outstock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId where 1=1
and t1.companyId = #{companyid,jdbcType=CHAR}

SELECT SUBSTRING(newtable.icode FROM 1 FOR 8) AS idate, SUM(newtable.inum) AS isnum, AVG(newtable.iprice) AS iaprice, SUM(newtable.itotal) AS istotal FROM ( SELECT t1.num AS inum, t1.price AS iprice, t1.num * t1.price AS itotal, t1.instockNum AS icode FROM t_instock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId WHERE 1 = 1
AND t1.companyId = #{companyid,jdbcType=CHAR}
AND t1.goodsId = #{goods,jdbcType=CHAR}
AND t1.instockNum LIKE CONCAT('${month}', '%')
) AS newtable GROUP BY idate

SELECT newtableall.* FROM ( ( SELECT productname AS iopn, SUBSTRING(newtable1.icode FROM 1 FOR 6) AS iodate, SUM(newtable1.inum) AS iosnum, AVG(newtable1.iprice) AS ioaprice, SUM(newtable1.itotal) AS iostotal, '入库' AS iotype FROM ( SELECT t3.productName AS productname, t1.num AS inum, t1.price AS iprice, t1.num * t1.price AS itotal, t1.instockNum AS icode FROM t_instock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId WHERE 1 = 1
AND t1.companyId = #{companyid,jdbcType=CHAR}
AND t1.instockNum LIKE CONCAT('${yearmonth}', '%')
) AS newtable1 GROUP BY iopn ORDER BY iopn ) UNION ( SELECT productname AS iopn, SUBSTRING(newtable2.ocode FROM 1 FOR 6) AS iodate, SUM(newtable2.onum) AS iosnum, AVG(newtable2.oprice) AS ioaprice, SUM(newtable2.ototal) AS iostotal, '出库' AS iotype FROM ( SELECT t3.productName AS productname, t1.num AS onum, t1.price AS oprice, t1.num * t1.price AS ototal, t1.outstockNum AS ocode FROM t_outstock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId WHERE 1 = 1
AND t1.companyId = #{companyid,jdbcType=CHAR}
AND t1.outstockNum LIKE CONCAT('${yearmonth}', '%')
) AS newtable2 GROUP BY iopn ORDER BY iopn ) ) AS newtableall ORDER BY newtableall.iotype

SELECT SUBSTRING(newtable.ocode FROM 1 FOR 8) AS odate, SUM(newtable.onum) AS osnum, AVG(newtable.oprice) AS oaprice, SUM(newtable.ototal) AS ostotal FROM ( SELECT t1.num AS onum, t1.price AS oprice, t1.num * t1.price AS ototal, t1.outstockNum AS ocode FROM t_outstock_log t1 LEFT JOIN t_goods t2 ON t1.goodsId = t2.goodsId LEFT JOIN t_product t3 ON t2.productId = t3.productId WHERE 1 = 1
AND t1.companyId = #{companyid,jdbcType=CHAR}
AND t1.goodsId = #{goods,jdbcType=CHAR}
AND t1.outstockNum LIKE CONCAT('${month}', '%')
) AS newtable GROUP BY odate
