mapper.select:
SELECT ti.instockMainId, ti.goodsId, p.productName, im.instockDate, im.instockBatchNum, yi.qcmaterialsURL, yi.id, yi.createTime, yi.createBy, yi.updateTime, yi.updateBy FROM t_instock_item ti INNER JOIN t_goods g ON ti.goodsId = g.goodsId INNER JOIN t_product p ON g.productId = p.productId INNER JOIN t_instock_main im ON ti.instockMainId = im.instockMainId LEFT JOIN t_instockqc yi ON ti.goodsId = yi.goodsId where 1=1
and im.companyId = #{instockqc.companyId,jdbcType=CHAR}
and im.instockDate > #{instockqc.instockDate1,jdbcType=TIMESTAMP}
and im.instockDate < #{instockqc.instockDate2,jdbcType=TIMESTAMP}
and im.instockBatchNum like CONCAT('${instockqc.instockBatchNum}','%' )
and yi.instockMainId=#{instockqc.instockMainId}
ORDER BY ti.createTime DESC

select * from t_instockqc


		instockMainId=#{instockMainId}


		and goodsId=#{goodsId}
