mapper.select:
select

from t_outstock_log
where 1 = 1


		and companyId = #{outstocklog.companyId,jdbcType=CHAR}


		and outstockNum = #{outstocklog.outstockNum,jdbcType=VARCHAR}


		and outstockBatchNum = #{outstocklog.outstockBatchNum,jdbcType=VARCHAR}


		and goodsId = #{outstocklog.goodsId,jdbcType=CHAR}
